const Tabs = ({ setTopic, topic }) => {
    console.log("topic is: ", topic);
    console.log("setTopic is: ", setTopic);
    return (
        <div style={{ zIndex: 30000 }}>
            <button
                onClick={() => {
                    console.log("clicked");
                    setTopic("Skills");
                }}
                className={
                    topic === "Skills" ? "tab-button active" : "tab-button"
                }
            >
                Skill Development
            </button>
            <button
                onClick={() => setTopic("Networking")}
                className={
                    topic === "Networking" ? "tab-button active" : "tab-button"
                }
            >
                Networking
            </button>
            <button
                onClick={() => setTopic("Career Advice")}
                className={
                    topic === "Career Advice"
                        ? "tab-button active"
                        : "tab-button"
                }
            >
                Career Advice
            </button>
            <button
                onClick={() => setTopic("All")}
                className={topic === "All" ? "tab-button active" : "tab-button"}
            >
                View All
            </button>
        </div>
    );
};

